mapper.select:
select
ID,PROJECT_NO,TASK_NO,TASK_NAME,CREATE_DATE,PLAN_DAYS,REMARK,STATUS
from REWARD_TASK_INFO


		and ID = #{id}


		and PROJECT_NO = #{projectNo}


		and TASK_NO = #{taskNo}


		and TASK_NAME = #{taskName}


		and CREATE_DATE = #{createDate}


		and PLAN_DAYS = #{planDays}


		and REMARK = #{remark}


		and STATUS = #{status}

SELECT
REWARD_TASK_INFO.ID,
REWARD_TASK_INFO.PROJECT_NO,
PROJECT_INFO.PROJECT_NAME,
REWARD_TASK_INFO.TASK_NO,
REWARD_TASK_INFO.TASK_NAME,
REWARD_TASK_INFO.CREATE_DATE,
REWARD_TASK_INFO.PLAN_DAYS,
REWARD_TASK_INFO.REMARK,
REWARD_TASK_INFO. STATUS
FROM
REWARD_TASK_INFO
LEFT JOIN PROJECT_INFO ON REWARD_TASK_INFO.PROJECT_NO =
PROJECT_INFO.PROJECT_NO


		and ID = #{id}


		and PROJECT_NO = #{projectNo}


		and TASK_NO = #{taskNo}


		and TASK_NAME = #{taskName}


		and CREATE_DATE = #{createDate}


		and PLAN_DAYS = #{planDays}


		and REMARK = #{remark}


		and STATUS = #{status}
